using System;
using System.Collections.Generic;
using System.Linq;
using System.Windows;

namespace KTool.Revit
{
    public partial class WorksetSelectorWindow : Window
    {
        public List<string> SelectedWorksets { get; private set; }

        public WorksetSelectorWindow(IList<string> worksets)
        {
            InitializeComponent();
            // ...existing code...
            WorksetListBox.ItemsSource = worksets;
        }

        private void Ok_Click(object sender, RoutedEventArgs e)
        {
            SelectedWorksets = WorksetListBox.SelectedItems.Cast<string>().ToList();
            DialogResult = true;
            Close();
        }

        private void Cancel_Click(object sender, RoutedEventArgs e)
        {
            DialogResult = false;
            Close();
        }
    }
}
